mapper.select:
select
id,mo_num,mo_tzdh,mo_zcnum,mo_mcid,mo_sl,mo_class,state,upzc,downzc,mo_order,mo_date
from bg_mozcauto where mo_isend=0 order by mo_date desc ;

select top 1
id,mo_num,mo_tzdh,mo_zcnum,mo_mcid,mo_sl,mo_class,state,upzc,downzc,mo_order,mo_date
from bg_mozcauto where mo_isend=0 order by mo_date desc ;

select
id,mo_num,mo_tzdh,mo_zcnum,mo_mcid,mo_sl,mo_class,state,upzc,downzc,mo_order,mo_date
from bg_mozcauto where mo_isend=0 and mo_tzdh=#{tzdh} ;
